//probably better to have a reduce here rather than a map
//just do a true ? true : false ; return true/false
function checkWin(board){
    let count = 0;
    count = board.reduce((acc, arr) => 
      acc + arr.reduce((acc, elem) => 
        !elem ? acc + 1 : acc+0 ));
  return count === 0 ? true : false;
  }


export { checkWin };
