import React, {Component} from "react";
import Cell from "./Components/Cell";
import './Board.css';

/** Game board of Lights out.
 *
 * Properties:
 *
 * - nrows: number of rows of board
 * - ncols: number of cols of board
 * - chanceLightStartsOn: float, chance any cell is lit at start of game
 *
 * State:
 *
 * - hasWon: boolean, true when board is all off
 * - board: array-of-arrays of true/false
 *
 *    For this board:
 *       .  .  .
 *       O  O  .     (where . is off, and O is on)
 *       .  .  .
 *
 *    This would be: [[f, f, f], [t, t, f], [f, f, f]]
 *
 *  This should render an HTML table of individual <Cell /> components.
 *
 *  This doesn't handle any clicks --- clicks are on individual cells
 *
 **/

class Board extends Component {
  //WILL DEPRECATE
  static defaultProps = {
    ncols : 3,
    nrows : 3,    
   }
  constructor(props) {
    super(props);
     // TODO: set initial state
    this.state =  {
      hasWon : false,
      chanceLightStartsOn : 0.4,
      board : [],
      ncols : 4,
      nrows : 4,
      test2 : [[1,2],[3,4]]
    } 
    this.flipCellsAround = this.flipCellsAround.bind(this);
    this.createBoard = this.createBoard.bind(this);
    this.gameClickHandler = this.gameClickHandler.bind(this);
    this.rowChange = this.rowChange.bind(this);
    this.colChange = this.colChange.bind(this);
  }

  /** create a board nrows high/ncols wide, each cell randomly lit or unlit -- MY NOTE: At least one MUST be lit*/

  createBoard() {
    let board = [];
    let newArr = [];
     // TODO: create array-of-arrays of true/false values
    for(let i = 0; i < this.state.ncols; i++, newArr = []) {
      for(let k = 0; k < this.state.nrows; k++) {
        newArr.push(Math.random() < this.state.chanceLightStartsOn ? true : false);
      }
      board.push(newArr);
    }
    this.setState({ board : board })   
  }
  gameClickHandler(event) {
    event.preventDefault()
    this.createBoard();
  }

  /** handle changing a cell: update board & determine if winner */

  flipCellsAround(coord) {
    let {ncols, nrows} = this.props;
    let board = this.state.board;
    let [y, x] = coord.split("-").map(Number);

    function flipCell(y, x) {
      // if this coord is actually on board, flip it

      if (x >= 0 && x < ncols && y >= 0 && y < nrows) {
        board[y][x] = !board[y][x];
      }
    }    

    // TODO: flip this cell and the cells around it

    // win when every cell is turned off
    // TODO: determine is the game has been won


    //CATCH THIS THIS ALREADY WAS HERE!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
    // this.setState({board, hasWon});
  }

  rowChange(event) {
    this.setState({
      nrows : event.target.value,
    });
  }
  colChange(event) {
    this.setState({
      ncols : event.target.value,
    });
  }


  /** Render game board or winning message. */

  render() {
    const createCell = this.state.board.map(arr=> arr.map(elem => <Cell isLit={elem} />)
    )
    return(
      <div>
        <form>          
          <input onChange={this.rowChange} placeholder="Enter Number of Rows" type="number" name="number of rows" value={this.state.nrows} required/>
          <input onChange={this.colChange} placeholder="Enter Number of Columns" type="number" name="number of columns" value={this.state.ncols} required/>  
          <div></div>
          <button onClick={this.gameClickHandler}>New Game</button>        
        </form>
        {/* <div>{this.state.board.map(arr => {arr.map(elem => <Cell isLit={elem} />))

        </div>         */}
        <div className="boardGrid">
        {createCell}   
        </div>  

      </div>
    )

    // if the game is won, just show a winning msg & render nothing else

